import React from "react";
import { Link } from "react-router-dom";

export const Articulo = ({ id, name, precio, image }) => {
    return (
        <div>
            <h3>{name}</h3>
            <p>Precio: {precio}</p>
            {/* Muestra la imagen usando la etiqueta <img> */}
            <img src={image} alt={name} style={{ maxWidth: '100%', height: 'auto' }} />
            
            <Link to={`/articulos/${id}`}>
                <button>Ver detalles</button>
            </Link>
        </div>
    );
}
