// ArticulosDetails.js
import React, { useContext } from 'react';
import { useParams } from 'react-router-dom';
import {ArticulosContext} from '../contex/ArticulosContext';
import {LinearProgress} from "@mui/material";
import '../Style/ArticulosDetails.css';
import { Link } from "react-router-dom";

console.log('Componente Overview renderizado');
const ArticulosDetails = () => {
    const { articulosId } = useParams();
    const { articulos } = useContext(ArticulosContext);
    const articulo = articulos.find(r => r.id === articulosId);
    
    if (!articulo) {
        
        return  <LinearProgress color="secondary"/>;
    }

    console.log(articulo.name);

    return (
        <div className="details-container">
            <h2 >{articulo.name}</h2>
            <p >Precio: {articulo.precio}</p>
            <img src={articulo.image} alt={articulo.name} className="details-image" />
            <p >Descripcion: {articulo.descripcion}</p> 
            <Link to={`/carArticulos/${articulo.id}`}>
                <button className="articulo-boton">Agregar</button>
            </Link>
        </div>
    );
}

export default ArticulosDetails;
