// AticulosDetails.js
import React, { useContext } from 'react';
import { useParams } from 'react-router-dom';
import {ArticulosContext} from '../contex/ArticulosContext';

const AticulosDetails = () => {
    const { articulosId } = useParams();
    const { articulos } = useContext(ArticulosContext);
    const articulo = articulos.find(r => r.id === articulosId);
    <h1 >Bienvenidos a UNIR Delivery1</h1>
    if (!articulo) {
        return <h2>Articulo no encontrado</h2>;
    }

    console.log(articulo.name);

    return (
        <div >
            <h2 >{articulo.name}</h2>
            <p >Precio: {articulo.valor}</p>
            
        </div>
    );
}

export default AticulosDetails;
