import React from "react";
import { Link } from "react-router-dom";
import './Articulo.css';  // Importa tu archivo de estilos CSS

const Articulo = ({ id, name, precio, imagen }) => {
    return (
        <div className="articulo-container">
            <h3>{name}</h3>
            <img src={imagen} alt={name} className="articulo-imagen" />
            <p className="articulo-precio">Precio: {precio}</p>
            
            <Link to={`/articulos/${id}`} className="articulo-link">
                <button className="articulo-boton">Ver detalles</button>
            </Link>
        </div>
    );
}

export default Articulo;
