import React, { useContext } from "react";
import { ArticulosContext } from "../hooks/ArticulosTienda";
import { Articulo } from "./Articulo"; // Asumí que tienes un componente llamado Articulo

export const Overview = () => {
  const { articulos } = useContext(ArticulosContext);

  return (
    <div>
      <h2>Articulos disponibles</h2>

      {articulos.length > 0 ? (
        articulos.map((articulo, index) => (
          <Articulo key={index} id={articulo.id} name={articulo.name} valor={articulo.valor} />
        ))
      ) : (
        <p>No hay artículos disponibles</p>
      )}
    </div>
  );
};
