import GlobalRouter from './GlobalRouter';

function App() {
  const { articulos } = ArticulosTienda({});

  return (
    <ArticulosContext.Provider value={{ articulos }}>
      <GlobalRouter></GlobalRouter>
    </ArticulosContext.Provider>
  );
}

