import React, {useContext} from 'react';
//import '../styles/styles.css';
import {Articulo} from "../components/Articulo";
//import {Header} from "../components/Header";
//import {Footer} from "../components/Footer";
import {ArticulosContext} from "../contex/ArticulosContext";
import {LinearProgress} from "@mui/material";
import '../Style/Articulo.css';

export const Overview = () => {
  
    const { articulos } = useContext(ArticulosContext);
    
    return (
        <div>
            <h2>Articulos Disponibles</h2>
            <div className="column-container">

                {
                    /**
                     * Por cada restaurante conocido
                     * Se crea un Restaurant con la informacion del restaurante
                     *
                     * Si los datos aun no se han recogido (lista de size 0) del back-end, se muestra un Spinner.
                     */
                    articulos.length > 0 ? (
                      articulos.map((articulo, index) => (
                            <div key={index} className="articulo-card">
                            <Articulo
                                key={index}
                                id={articulo.id}
                                name={articulo.name}
                                precio={articulo.precio}
                                imagen={articulo.image}
                               
                            />
                            </div>
                        ))
                    ) : (
                        <LinearProgress color="secondary"/>
                    )
                }

                {}
            </div>
        </div>
    );
}
