import React, { useContext } from 'react';
import { useParams } from 'react-router-dom';
import { ArticulosContext } from '../contex/ArticulosContext';
import '../Style/ReservaPage.css'; // Asegúrate de tener tu archivo de estilos para la página de reserva

const ReservaPage = () => {
  const { articulosId } = useParams();
  const { articulos } = useContext(ArticulosContext);
  const articulo = articulos.find((r) => r.id === articulosId);

  if (!articulo) {
    return <div>Error: Artículo no encontrado</div>; // Puedes personalizar este mensaje de error
  }

  return (
    <div className="reserva-container">
      <h2>{articulo.name}</h2>
      <p>Precio: {articulo.precio}</p>
      <img src={articulo.image} alt={articulo.name} className="reserva-image" />
      <p>Descripción: {articulo.descripcion}</p>
      {/* Aquí puedes agregar más campos y lógica relacionada con la reserva */}
    </div>
  );
};

export default ReservaPage;
