// En el archivo GlobalRouter.js

import React from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Landing from './views/Landing';
import ArticulosDetails from './views/ArticulosDetails';
//import NotFound from '../views/NotFound';
import {Overview} from "./views/Overview";
import {Header} from "./components/Header";

function GlobalRouter() {
    return (
        
        <BrowserRouter>
        
            <Routes>
                
                <Route path="/" element={<Landing />} />
                <Route path="/articulos" element={<Layout><Overview /></Layout>} />
                <Route path="/articulos/:articulosId" element={<Layout><ArticulosDetails /></Layout>} />
                
            </Routes>
        </BrowserRouter>
    );
}

const Layout = ({children}) => (
    <>
        <Header />
        {children}
    </>
);

export default GlobalRouter;
