// En el archivo ArticulosTienda.js
import React, { createContext, useEffect, useState } from 'react';

export const ArticulosContext = createContext();

export const ArticulosTienda = () => {
  const [articulos, setArticulos] = useState([]);

  useEffect(() => {
    const fetchData = () => {
      setTimeout(() => {
        setArticulos([
          { id: '1', name: 'Camisa', valor: 2000, image: 'url_camisa.jpg' },
          { id: '2', name: 'Pantalón', valor: 2500, image: 'url_pantalon.jpg' },
          { id: '3', name: 'Zapatos', valor: 4000, image: 'url_zapatos.jpg' },
        ]);
      }, 2500);
    };

    fetchData();
  }, []);

  return <ArticulosContext.Provider value={{ articulos }} />;
};
