import React, { useEffect, useState } from 'react';

export const ArticulosTiendas = () => {
    const [articulos, setArticulos] = useState([]);

    useEffect(() => {
        const fetchData = () => {
            // Simular una solicitud de API o cualquier operación asíncrona
            setTimeout(() => {
                // Construir una lista de artículos
                setArticulos([
                    { id: "1", name: "Camisa", valor: 2000 },
                    { id: "2", name: "Pantalón", valor: 2500 },
                    { id: "3", name: "Zapatos", valor: 4000 },
                    // Agrega más artículos según sea necesario
                ]);
            }, 2500);
        };

        fetchData();
    }, []); // Array de dependencias vacío para asegurar que el useEffect se ejecute solo una vez al montar el componente

    return articulos;
};
