import { useEffect, useState } from "react";

export const ArticulosTienda = ({ searchTerm }) => {
  const [articulos, setArticulos] = useState([]);
  
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`https://www.googleapis.com/books/v1/volumes?q=${searchTerm}`);
        const data = await response.json();

        // Extrae la informaci칩n relevante de los libros
        const libros = data.items.map((item) => ({
          id: item.id,
          name: item.volumeInfo.title,
          precio: item.saleInfo.listPrice?.amount || 0, // Precio, o 0 si no est치 disponible
          image: item.image, // Imagen, o una por defecto
        }));

        setArticulos(libros);
      } catch (error) {
        console.error("Error al obtener datos:", error);
      }
    };

    fetchData();
  }, [searchTerm]); // Agrega searchTerm como dependencia para que la solicitud se actualice cuando cambie

  return articulos;
};
