import {useEffect, useState} from "react";

export const ArticulosTienda = () => {

    const [restaurants, setArticulos] = useState([]);
    /**
     * Se hace uso de useEffect para definir un efecto de montaje que traerá la información de ingredientes
     * del back-end en el primer renderizado.
     */
    useEffect(() => {
        //fetch(process.env.REACT_APP_GW_URL).then((res) => res.json()).then((res) => setRestaurants(res));

        setTimeout(() => {
            setArticulos([
                { id: '1', name: 'Camisa', valor: 2000, image: 'url_camisa.jpg' },
          { id: '2', name: 'Pantalón', valor: 2500, image: 'url_pantalon.jpg' },
          { id: '3', name: 'Zapatos', valor: 4000, image: 'url_zapatos.jpg' },
            ]);
        }, 2500);
    }, []);

    return restaurants;
}
