// En el archivo Overview.js
import React, { useContext } from "react";
import { Link } from 'react-router-dom';
import { ArticulosContext } from "../hooks/ArticulosTienda";

export const Overview = () => {
  const  articulos  = useContext(ArticulosContext);

  return (
    <div>
      <h2>Articulos disponibles</h2>

      {articulos.length > 0 ? (
        <div>
          <ul>
            {articulos.map((articulo) => (
              <li key={articulo.id}>
                {`ID: ${articulo.id}, Name: ${articulo.name}, Valor: ${articulo.valor}`}
              </li>
            ))}
          </ul>
          {/* Agrega un enlace a la página de artículos */}
          <Link to="/Overview">Ir a Artículos</Link>
        </div>
      ) : (
        <p>Cargando artículos...</p>
      )}
    </div>
  );
};

export default Overview;
