import React, { useEffect, useState } from "react";
import { getProducts } from "../services/productService";
import { ProductCardView } from "./ProductCardView";
import { Navbar } from "../components/Navbar";
import '../Style/CatalogView.css'; // Archivo de estilo para el banner

const CatalogView = ({ handler }) => {
    const [products, setProducts] = useState([]);
    const productsPerPage = 6;
    const [currentGroup, setCurrentGroup] = useState(1);

    useEffect(() => {
        setProducts(getProducts());
    }, []);

    const startProductIndex = (currentGroup - 1) * productsPerPage;
    const endProductIndex = startProductIndex + productsPerPage;
    const currentProducts = products.slice(startProductIndex, endProductIndex);

    const totalPages = Math.ceil(products.length / productsPerPage);

    const nextPage = () => {
        setCurrentGroup((prevGroup) => Math.min(prevGroup + 1, totalPages));
    };

    const prevPage = () => {
        setCurrentGroup((prevGroup) => Math.max(prevGroup - 1, 1));
    };

    return (
        <>
            <Navbar />
            <div className="row">
                {currentProducts.map(prod => (
                    <div className="cotenedor" key={prod.id}>
                        <ProductCardView
                            handler={handler}
                            id={prod.id}
                            name={prod.name}
                            image={prod.image}
                            description={prod.description}
                            price={prod.price}
                        />
                    </div>
                ))}
            </div>

            <div className="pagination">
                {currentGroup > 1 && (
                    <button className="btn btn-secondary" onClick={prevPage}>
                        Anterior
                    </button>
                )}

                {currentGroup < totalPages && (
                    <button className="btn btn-secondary" onClick={nextPage}>
                        Siguiente
                    </button>
                )}
            </div>
        </>
    );
};

export default CatalogView;
