import React from "react";
import { Link } from "react-router-dom";
import '../Style/Articulo.css';

export const Articulo = ({ id, name, precio, imagen }) => {
    return (
        <div className="articulo-conta">
            <h4 className="articulo-name">{name}</h4>
            <div>
                <img src={imagen} alt={name} className="articulo-imagen"/>
                
            </div>
            <p className="articulo-precio">Precio: {precio}</p>
                
                <Link to={`/articulos/${id}`}>
                    <button className="articulo-boton">Ver detalles</button>
                </Link>
        </div>
    );
}
