import { useEffect, useState } from "react";

export const ArticulosTienda = () => {
  const [articulos, setArticulos] = useState([]);
  // eslint-disable-next-line react-hooks/exhaustive-deps
  const items = ['sistema', 'c', 'automatización', 'python', 'c#', 'sql', '.net'];

  useEffect(() => {
    const fetchData = async () => {
      try {
        const searchPromises = items.map(async (item) => {
          const response = await fetch(`https://www.googleapis.com/books/v1/volumes?q=${item}`);
          const data = await response.json();

          if (data.items) {
            const librosConImagen = data.items
              .filter((book) => book.volumeInfo.imageLinks?.thumbnail)
              .map((book) => ({
                id: book.id || "idPredeterminado",
                name: book.volumeInfo.title || "Sin título",
                descripcion: book.volumeInfo.description || "Sin descripción disponible",
                precio: book.saleInfo.listPrice?.amount || 1000,
                image: book.volumeInfo.imageLinks.thumbnail,
              }));

            return librosConImagen;
          }
        });

        const resultados = await Promise.race([
          Promise.all(searchPromises),
          new Promise((_, reject) => setTimeout(() => reject("Tiempo de espera agotado"), 5000)),
        ]);

        const libros = resultados.flat();

        setArticulos(libros);
      } catch (error) {
        console.error("Error al obtener datos:", error);
      }
    };

    fetchData();
  }, [items]);

  return (
    <div>
      {articulos.length > 0 ? (
        <ul>
          {articulos.map((articulo) => (
            <li key={articulo.id}>
              <p>ID: {articulo.id}</p>
              <p>Nombre: {articulo.name}</p>
              <p>Descripción: {articulo.descripcion}</p>
              <p>Precio: {articulo.precio}</p>
              <img src={articulo.image} alt={articulo.name} />
            </li>
          ))}
        </ul>
      ) : (
        <p>No se encontraron artículos</p>
      )}
    </div>
  );
};
