import React from "react";
import { Link } from "react-router-dom";

export const Articulo = ({ id, name, precio }) => {
    return (
        <div>
            <h3>{name}</h3>
            <p>Precio: {precio}</p>
            <Link to={`/articulo/${id}`}>
                <button>Ver detalles</button>
            </Link>
        </div>
    );
}
